<div class="col-sm-4 product no-push">
	<div class="row">
		<div class="col-xs-8 col-xs-offset-2">
			<div class="grid-image img-circle imgLiquid_bgSize imgLiquid_ready">
				<img src="~{img}~" alt="">
			</div>
		</div>
	</div>

	<div class="row text-center">
		<div class="col-xs-10 col-xs-offset-1">
			<div class="row">
				<div class="col-xs-10 col-xs-offset-1">
					<div class="text-center list-title">~{name}~</div>
					<div class="list-desc" style="height: auto;">~{description}~</div>
					<div class="list-title" style="height: auto;">
						<span class="line-through price-old">~{old-price}~</span>
						<span class="price">~{new-price}~</span>
					</div>
				</div>
			</div>
		</div>
	</div>
</div>